import './App.css';
import React from "react";
import Table from "./components/Table";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      loaded: false
    };
  }

  formatRows() {
    let buy_rows = [];
    let sell_rows = [];
    let rec_rows = []
    for (const coin of this.state.coins) {
      let b_row = [coin.ticker];
      let s_row = [coin.ticker];
      let r_row = [coin.ticker];
      for (const [key,value] of Object.entries(coin.buy_prices)) {
        b_row.push(value);
      }
      for (const [key,value] of Object.entries(coin.sell_prices)) {
        s_row.push(value);
      }
      r_row.push(coin.buy_rec);
      r_row.push(coin.sell_rec);

      buy_rows.push(b_row);
      sell_rows.push(s_row);
      rec_rows.push(r_row);
    }
    return [buy_rows, sell_rows, rec_rows];
  }

  componentDidMount() {
    fetch("http://localhost:8080/coinexchange")
    .then(res => res.json())
    .then(
        (res) => {
          this.setState({
            exchanges: res[0],
            coins: res[1],
            loaded: true
          });
        }
    );
  }

  render() {
    console.log(this.state);
    let buy_rows = []
    let sell_rows = []
    let rec_rows = []
    const rec_header = ["Buy", "Sell"]
    if (this.state.loaded) {
      [buy_rows, sell_rows, rec_rows] = this.formatRows();
      return (
        <div>
          <Table title="Buy Prices" exchanges={this.state.exchanges} rows={buy_rows} />
          <Table title="Sell Prices" exchanges={this.state.exchanges} rows={sell_rows} />
          <Table title="Recommendations" exchanges={rec_headers} rows={sell_rows} />
        </div>
      );
    } else {
      return("Loading...")
    }
  }
}


export default App;
