import './App.css';
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      loaded: false
    };
  }

  formatRows() {
    let buy_rows = [];
    let sell_rows = [];
    for (const coin of this.state.coins) {
      let b_row = [coin.ticker];
      let s_row = [coin.ticker];
      for (const [key,value] of coin.buy_prices) {
        b_row.push(value);
      }
      for (const [key,value] of coin.sell_prices) {
        s_row.push(value);
      }
      buy_rows.push(b_row);
      sell_rows.push(s_row);
    }
    return [buy_rows, sell_rows];
  }

  componentDidMount() {
    fetch("http://localhost:8080/coinexchange")
    .then(res => res.json())
    .then(
        (res) => {
          this.setState({
            exchanges: res[0],
            coins: res[1]
          });
        }
      );
    this.setState({loaded: true});
  }

  render() {
    console.log(this.state);
    if (this.state.loaded) {
    const [buy_rows, sell_rows] = this.formatRows();
    console.log("buy rows: ");
    console.log(buy_rows);
    console.log("sell rows: ");
    console.log(sell_rows);
  }
    return (
      <div>
        { this.state.exchanges }
        { this.state.coins }
      </div>
    );
  }
}


export default App;
